import "./profile.scss";
import FacebookTwoToneIcon from "@mui/icons-material/FacebookTwoTone";
import LinkedInIcon from "@mui/icons-material/LinkedIn";
import InstagramIcon from "@mui/icons-material/Instagram";
import PinterestIcon from "@mui/icons-material/Pinterest";
import TwitterIcon from "@mui/icons-material/Twitter";
import PlaceIcon from "@mui/icons-material/Place";
import LanguageIcon from "@mui/icons-material/Language";
import EmailOutlinedIcon from "@mui/icons-material/EmailOutlined";
import MoreVertIcon from "@mui/icons-material/MoreVert";
import Posts from "../../components/posts/Posts"
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { makeRequest  } from "../../axios";
import { useLocation, } from "react-router-dom";
import { useContext } from "react";
import { AuthContext } from "../../context/authContext";

const Profile = () => {
  const { currentUser } = useContext(AuthContext);

  const userId = parseInt(useLocation().pathname.split("/")[2]);
  if (isNaN(userId) || userId <= 0) {
  // Defina um valor padrão ou retorne um erro
  console.error("userId inválido");
  return res.status(400).json("UserId inválido");
}
  const { isPending, error, data } = useQuery({
    queryKey: ['user'],
    queryFn: () =>
      makeRequest.get("/users/find/"+ userId ).then((res) => {
        return res.data;
      })
  })

  const { data: relationshipData } = useQuery({
    queryKey: ['relationship'],
    queryFn: () =>
      makeRequest.get("relationships?followedUserId="+ userId ).then((res) => {
        return res.data;
      })
  })

  const queryClient = useQueryClient()

  const mutation = useMutation({
    mutationFn: (following) => {
      if (following) return makeRequest.delete("/relationships?userId="+ userId)
      return makeRequest.post("/relationships",{userId})
    },
    onSuccess: () => {
      // Invalidate and refetch
      queryClient.invalidateQueries({ queryKey: ["relationship"] })
    },
  })


  const handleFollow = () => {
    mutation.mutate(relationshipData && relationshipData.includes(currentUser.id) )

  }
  
 
  

  return (
    <div className="profile">
      <div className="images">
        <img
          src={data && data.coverPic}
          alt=""
          className="cover"
        />
        <img
          src={data && data.profilePic}
          alt=""
          className="profilePic"
        />
      </div>
      <div className="profileContainer">
        <div className="uInfo">
          <div className="left">
          </div>
          <div className="center">
            <span>{data && data.name}</span>
            <div className="info">
              <div className="item">
                <PlaceIcon />
                <span>USA</span>
              </div>
              <div className="item">
                <LanguageIcon />
                <span>lama.dev</span>
              </div>
            </div>
            {userId === currentUser.id ? (
            <button> update </button>
            ) : (
            <button onClick ={handleFollow}>{relationshipData && relationshipData.includes(currentUser.id) ? "Seguindo" : "Seguir"}</button>
            )}
          </div>
          <div className="right">
            <EmailOutlinedIcon />
            <MoreVertIcon />
          </div>
        </div>
      <Posts userId={userId}/>
      </div>
    </div>
  );
};

export default Profile;
