import { useState } from 'react';
import { Link, useLocation } from "react-router-dom";
import "./updateuser.scss";
import axios from "axios";
import { makeRequest } from '../../axios';
import { useMutation, useQueryClient } from '@tanstack/react-query';



const UpdateUser = () => {

  const userId= parseInt(useLocation().pathname.split("/")[2])

  const [texts, setTexts] = useState({
    name:"",
  })

  const[cover,setCover] = useState(null)

  const[profile,setProfile] = useState(null)

  const upload = async (file) => {
    try{
      const formData = new FormData();
      formData.append("file", file)
      const res = await makeRequest.post("/upload", formData);
      return res.data
    } catch(err){
      console.log(err)
    }

  }

  const [err, setErr] = useState(null);

  const handleChange = (e) =>{
    setTexts(prev=>({...prev, [e.target.name]: e.target.value }))
  };
  
  const handleCoverChange = (e) => {
    setCover(e.target.files[0]);
  };

  const handleProfileChange = (e) => {
    setProfile(e.target.files[0]);
  };
  const handleClick = async (e) =>{
    e.preventDefault();
    let coverUrl = userId.coverPic;
    let profileUrl = userId.ProfilePic;

    if (cover) {
        coverUrl = await upload(cover);
      }
  
      if (profile) {
        profileUrl = await upload(profile);
      }

   
    try{
        await axios.post("http://localhost:8800/api/auth/updateuser",{
            ...texts,
            coverPic: coverUrl,
            ProfilePic: profileUrl,
        });
    } catch(err){
      setErr(err.response.data);
    }
  }


  return (
    <div className="register">
      <div className="card">
        <div className="left">
          <h1>Uni4Life</h1>
          <p>
            Faça login para ser direcionado a rede social de sua universidade!
          </p>
          <span>Você já possui conta?</span>
          <Link to="/login">
          <button>Login</button>
          </Link>
        </div>
        <div className="right">
          <h1>O que deseja atualizar?</h1>
          <form>
            <input type="text" placeholder="Nome de exibição" name="name" onChange={handleChange}  />
            <input type="password" placeholder="Senha" name="password" onChange={handleChange}  />
            <p>Escolha uma nova imagem de capa:</p>
            <input type="file" accept="image/*" onChange={handleCoverChange} />
            <p>Escolha uma nova imagem de perfil:</p>
            <input type="file" accept="image/*" onChange={handleProfileChange} />
            {err && err}
            <button onClick={handleClick}>Atualizar Informações</button>
          </form>
          <Link to={`/profile/${userId}`}>
          <button>Voltar</button>
          </Link>
        </div>
      </div>
    </div>
  );
};
export default UpdateUser;
